void mainImage( out vec4 C, in vec2 U )
{
    vec2 R = iResolution.xy;
    vec3 c = texture(iChannel0, U/R).rgb*.1;
    c *= 2.-length((U+U-R)/R.y);
    C = vec4(c*c, 1);
}

// updated with Fabrice's hex golf seen here: https://www.shadertoy.com/view/DlSyDy
void mainImage(out vec4 C, in vec2 U)
{
    vec2 R = iResolution.xy,
         u = (U+U-R)/R.y*20./(1.-iMouse.y/R.y),
         h = vec2(.5, .866), 
         a = mod(u  , h+h) - h,
         b = mod(u-h, h+h) - h,
         g = dot(a,a) < dot(b,b) ? a : b,
         k = abs(g),
         v = abs(fract(U/R+.5)-.5)/fwidth(U/R);
    float t = pow(length(u-g), 2.)*iDate.w/3600.,
          f = (1.-max(k.x, dot(k, h))*2.);
    vec3 c = texture(iChannel0, mix(U/R, vec2(.5), -.05/length(u))).rgb*.97,
         j = cos(vec3(0, 1, 2)+t*6.2832)*.5+.5;
    c += 4.5*exp(-9.*max(f-.5, .8-f)) * ((sin(t*6.2832)/2.+.5) + j*j*.2);
    C = vec4(min(c, min(v.x, v.y)*.7), 1);
}
