void scene(in vec2 uv,inout vec3 col,in vec2 pos){
    float f=length(uv-pos);
    col=mix(col,vec3(1.,1.,0.),sqrt(smoothstep(0.03,0.02,f)));
    vec2 q=uv-vec2(0.5,0.1);
    q*=rot(-2.*PI*M.x/R.x+PI);
    float b=step(B(q),0.)+step(T(q-vec2(0.,0.08),0.03),0.);
    col=mix(col,vec3(0.,1.,1.),b);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    uv.x-=(iResolution.x/iResolution.y-1.)*0.5;
    vec3 col=mix(vec3(0.7),vec3(0.2),max(step(0.87,abs(uv.x-0.5)*2.),step(0.87,abs(uv.y-0.5)*2.)));//Background
    col=mix(col,vec3(1.),step(1.,abs(uv.x*2.-1.)));
    vec2 pos=texture(iChannel0,fragCoord).xy;
    scene(uv,col,pos);
    fragColor = vec4(col,1.0);
}

#define dt 0.01
#define invR 1./iResolution.x

const float e=30.;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 p=texture(iChannel0,uv);
    vec2 f=vec2(0.,0.5)*rot(2.*PI*iMouse.x/iResolution.x-PI);
    if(iFrame<5){
        p.xy=vec2(0.5,0.5);
    }

    vec2 pr=p.xy*2.-1.;
    if (max(abs(pr.x),abs(pr.y))<0.8){
        f=f;
    }
    else if(abs(pr.x)>=0.8){
        f=vec2(-sign(pr.x),0.)*e;
    }
    else if(abs(pr.y)>=0.8){
        f=vec2(0.,-sign(pr.y))*e;
    }

    p.zw+=f*dt;
    p.xy+=p.zw*dt;
    p.xy=clamp(p.xy,0.1-invR,0.9+invR);

    fragColor=p;
}

#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define PI 3.141592
#define M iMouse
#define R iResolution
float B(vec2 p){
    vec2 q=abs(p)-vec2(0.005,0.08);
    return length(max(q,0.))+min(max(q.x,q.y),0.);
}
//https://iquilezles.org/articles/distfunctions2d/
float T( in vec2 p, in float r )
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - r;
    p.y = p.y + r/k;
    if( p.x+k*p.y>0.0 ) p = vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0*r, 0.0 );
    return -length(p)*sign(p.y);
}
