// range between [0, 1]
float multiplier = 0.1;

// zoom amount
float zoom = 25.0;

// animation offset
vec2 move = vec2(0.0, 0.0);

float shape(vec2 uv)
{
    float normalizedSize = multiplier * 0.5;

    float left = step(normalizedSize, uv.x);
    float bottom = step(normalizedSize, uv.y);
    float up = step(normalizedSize, 1.0 - uv.y);
    float right = step(normalizedSize, 1.0 - uv.x);

    return left * bottom * up * right;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    move.x += iTime;

    vec2 uv = fragCoord / iResolution.xy;
    uv = uv * zoom + move;

    float cube = shape(fract(uv));
    cube = 1.0 - cube;

    fragColor = vec4(0.0, 1.0, 0.0, 1.0) * cube;
}
