void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = (2.0 * fragCoord - iResolution.xy) / min(iResolution.y, iResolution.x);
    float r = length(p);


    float flower = sin(6.0 * atan(p.y, p.x)+ iTime*0.3) * (0.04 + 0.8 * smoothstep(0.8, (1.0 + cos(iTime)), r)) ;


    vec3 fillColor = vec3(0.925,0.784,0.275);
    vec3 bgColor = vec3(0.180,0.212,0.220);
    float strokeWidth = 0.02 + 0.01 * sin(iTime);

    //vec3 flowerColor = vec3(0.1 + 0.5 * sin(iTime), 0.1 + 0.5 * cos(iTime), 0.1 + 0.5 * sin(iTime + 1.0));


    float distance = abs(flower) - strokeWidth;
    vec3 color = mix(fillColor, bgColor, smoothstep(0.0, strokeWidth, distance));

    //color = mix(color, flowerColor, smoothstep(0.0, 0.1, distance));

    fragColor = vec4(color, 1.0);
}
