#define X(T)+ t) * (1. - i * .01 * (sin(T) * .5 + .5))

void mainImage(out vec4 O, vec2 F)
{
    vec2 R = iResolution.xy,
         u = (F + F - R) / R.y * 1.4;

    // Apply scaling factor
    float scaleFactor = 0.8; // Adjust this factor to control the scale
    u *= scaleFactor;

    //Declares and initializes some variables: i (used in the loop), t (current time), and l (length of vector u).
    float i, t = iTime, l = length(u);

    for (O *= i; i < 100.0; i += .5)
    {
        O += pow(0.0015 / 
        abs(length(u + vec2(cos(.98 * i X(t+1.0), sin(i X(t)))
        - l*0.015 - .002) 
        * smoothstep(-2.0, 2.0, l) 
        * (1.0 * cos(i + i + l + l + t * .5 + 5. + vec4(0, 1, 2, 0))), O - O + 1.1);
        }

}
