uniform float Time;
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;
    float r = fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453);
    float g = fract(sin(dot(uv, vec2(23.1407, 45.567))) * 23456.7890);
    float b = fract(sin(dot(uv, vec2(34.2735, 90.789))) * 67890.1234);
    float timeFactor = sin(iTime);
    r *= timeFactor;
    g *= timeFactor;
    b *= timeFactor;
    fragColor = vec4(r, g, b, 1.0);
}
