void mainImage(out vec4 O, in vec2 F)
{
    vec2 R = iResolution.xy,
         u = (F+F-R)/R.y/.2;
    float i = 0., t = iTime;

    float audioAmplitude = texture(iChannel0, vec2(0.7)).r * 26.0; // Get audio amplitude and adjust strength
    float smoothedAmplitude = smoothstep(0.2, 0.4, audioAmplitude);

    for (O *= i; i < 1e2; i += .4)
        O += .002 / abs(length(u - sin(t*.1-i) +
             vec2(cos(i-+t))) + sin(i*01.+t*02.)+.5) * 
             (1.25 + cos(i+5. * t + length(u)/2. * vec4(1,2,3,5))) * smoothedAmplitude,
        u *= mat2(sin(5.11 - vec4(0,33,11,0)));
}
