void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized coordinates
    vec2 uv = fragCoord/iResolution.xy;

    // Time
    float time = iTime;

    // Sine wave 1
    float frequency1 = 3.0;
    float amplitude1 = sin(iTime * 0.5) * 0.5;
    float sinWave1 = amplitude1 * sin(2.0 * 3.14159 * frequency1 * uv.x + time);

    // Sine wave 2
    float frequency2 = 3.0;
    float amplitude2 = 0.3;
    float sinWave2 = amplitude2 * sin(2.0 * 3.14159 * frequency2 * (uv.x + sinWave1) + time);

    // Combine sine waves
    float combinedSinWave = sinWave1 + sinWave2;

    // Noise
    float noise = texture(iChannel0, uv).r;

    // Final color
    vec3 color = vec3(combinedSinWave + noise);

    // Output to the screen
    fragColor = vec4(color, 1.0);
}
