// Thanks to iapafoto for helping minify it
#define S(x) abs(x - round(x))

void mainImage( out vec4 k, in vec2 c)
{
    vec2 u = c / iResolution.x;
    float f, D = 1., i = D, p = D, T = S(iTime * .1) * 20.;
    for (; i++ < T; p += p)
        f = S(u.x * p) / p, // Take triangle waves w/ power of 2 frequencies as the lines
        D = min(D, abs(u.y - f));

    k = vec4(1.) - 4e2 * min(D, abs(u.y - mix(f, S(u.x * p) / p, fract(T)))); // Draw wave
}
