#define RED vec3(0.8,0.05,0.0)
#define ORANGE vec3(1.0,0.45,0. )
#define YELLOW vec3(1.0,0.9,0.1 )*0.9
#define PINK vec3(0.8,0.6,1.0 )*0.85
#define BLUE vec3(0.2,0.1,1.0 )

vec3 neon_line(vec2 uv, vec3 color, float t, float p){
    float i = 0.98;
    float d = abs(p-uv.x+uv.y*0.6)*0.4;
    d = t*0.01/d;
    d = (d-1.)/i+1.;
    d = clamp(d, 0., 1.);
    return color * d;
}

float f1(float x){
    x = fract(x)-clamp(fract(x),0.0,0.166)-clamp(fract(x),0.3333,0.833)+0.1666;
    x*= 16.;
    return x*x*x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.y = uv.y * iResolution.y/iResolution.x;

    uv*= 1.8;

    float ct = iTime * 0.2;
    vec3 red    = neon_line(uv, RED   , 2., -.4+f1(ct+0.025));
    vec3 orange = neon_line(uv, ORANGE, 2., -.2+f1(ct+0.050));
    vec3 yellow = neon_line(uv, YELLOW, 2., 0.0+f1(ct+0.075));
    vec3 pink   = neon_line(uv, PINK  , 2., 0.2+f1(ct+0.100));
    vec3 blue   = neon_line(uv, BLUE  , 2., 0.4+f1(ct+0.125));
    fragColor.xyz = red + orange + yellow + pink + blue;
}
