float sdHexagon( in vec2 p, in float r )
{
    const vec3 k = vec3(-0.866025404,0.5,0.577350269);
    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
    p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
    return length(p)*sign(p.y);
}

vec3 palette( in float t)
{
    vec3 a = vec3(0.650, 0.500, 0.310);
    vec3 b = vec3(-0.650, 0.500, 0.600);
    vec3 c = vec3(0.333, 0.278, 0.278);
    vec3 d = vec3(0.660, 0.000, 0.667);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     vec2 p = (fragCoord*2.0-iResolution.xy)/iResolution.y;
     vec2 p0 =p;
     vec3 finalColor= vec3(0.0);


     for(float i=0.0;i<2.3; i++){
      float x = (sin(iTime))*.5+1.; 
        p=fract(p*1.332)-0.5;
        float d = sdHexagon( p, 0.5 )* exp(-sdHexagon(p0,0.5))*x;

        vec3 col =palette((length(p0)+(cos(iTime*2.)*.6))/1.8);

        d = sin(-d*8.0+iTime)/8.;
        d = abs(d);
        d = smoothstep(0.0,0.1,d);
        d=0.095/d;
        finalColor = col *d;
     }
     fragColor = vec4(finalColor,1);


}
