// acid test from "Broken initialization" by kishimisu. https://shadertoy.com/view/clKXRR

void mainImage(out vec4 I, vec2 u) {

    vec2 R = iResolution.xy,
         p =  u/R;

    float T = iTime, a;
#if 1                      // 
    if (u.x <  R.x/2. )    // left: on OpenGL Chrome, it does p -= !
        p.y -= T = iTime;
    else                   // right
        p.y -= T;
#else
    a = p.y;               // this form works
    if (u.x <  R.x/2. )    // left
        a -= T = iTime;
    else                   // right
        a -= T;
    p.y = a;
#endif

    I = vec4(p,0,1);     
}
