//Polar Coordinates
//Art of Code Tutorial
//https://www.youtube.com/watch?v=r1UOB8NVE8I


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
vec2 st = vec2(atan(uv.x,uv.y),length(uv));
float nubPetals = 10.;
float twist = 0.3;
float radius = 0.9;
float size = .03;
float Pi2 = 6.2831;
twist = st.y*twist;
uv= vec2(st.x/(Pi2)+.5+ twist,st.y);
float x = uv.x*nubPetals;
float m = min(fract(x),fract(1.-x));
float c = smoothstep(0., 0.01, m*radius+size-uv.y);

fragColor = vec4(c);
}
