#define tau 6.28318

float zoom_out = 20.0f;
float amount = 20.0f;
float twist = 0.5f;
float speed = 1.0f;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy/iResolution.x - vec2(1.0,iResolution.y/iResolution.x) / 2.0) * zoom_out;
    if ( mod(floor(length(uv))+floor(atan(uv.y, uv.x)/tau*amount - iTime*speed + log(length(uv)) / log(1.0/twist)), 2.0) < 1.0 ) {
        fragColor = vec4( 0.0, 0.0, 0.0, 1.0 );
    } else {
        fragColor = vec4( 1.0, 1.0, 1.0, 1.0 );
    }
}
