float Square=80.0;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float time=mod(iTime,10.0);

    float count=time*Square;

    uv=floor(uv*count)/count;

    fragColor = texture(iChannel0,uv);
