// inspirado por https://www.shadertoy.com/view/7tyXzV
float sdfCircle(vec2 uv, float r, vec2 c) {
    return length(uv - c) - r;
}

float sdfSquare(vec2 uv, float size, vec2 c) {
    float x = uv.x - c.x;
    float y = uv.y - c.y;
    return max(abs(x), abs(y)) - size;
}

float sdfStar(vec2 uv, float size, vec2 c)
{
    vec2 suv = uv + c;
    suv.x = abs(suv.x);
    float a = 6.2832/5.;
    float d1 = dot(suv, vec2(sin(a), cos(a)));
    a = 3. * 6.2832/5.;
    float d2 = dot(suv, vec2(sin(a), cos(a)));
    a = 2. * 6.2832/5.;
    float d4 = dot(suv, vec2(sin(a), cos(a)));

    float d = min(max(d1, d2), max(suv.y, d4));
    float w = fwidth(d);
    return smoothstep(w, -w, d - size);
}

vec3 drawScene(vec2 uv) {
    float circle = sdfCircle(uv, 0.2, vec2(0, 0));
    float circle2 = sdfCircle(uv, 0.4, vec2(0, 0));
    vec3 col = vec3(1);
    float res = max(-circle, circle2);
    col = mix(vec3(0, 0, 1), col, step(0., res));

    float star = sdfStar(uv, 0.03, vec2( 8.*sin(2.* iTime)/27., 8.*cos(2.* iTime)/27.));
    //col = mix(vec3(1, 0, 0), col, step(0., star));
    col += star;
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    vec3 col = drawScene(uv);

    fragColor = vec4(col,1.0);
}
