void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec3 col = vec3(0);
    vec2 M = vec2(cos(iTime * 0.5), sin(iTime)) * 0.4;

    float n = max(4.0, iMouse.x / 10.0);

    uv *= n;
    vec2 pid = floor(uv);
    uv = fract(uv);

    float c = length(M - (pid / n)) * 2.0;

    col = vec3(0.5, 0.2, 0.8 * c) * (1.0 - c);

    // Output to screen
    fragColor = vec4(col,1.0);
}
