vec3 palette(in float t)
{
//[[0.216 0.499 0.618] [0.261 0.564 0.421] [0.387 0.476 1.523] [4.076 1.017 1.721]]
    vec3 a = vec3(0.216, 0.499, 0.618);
    vec3 b = vec3(0.261, 0.564, 0.421);
    vec3 c = vec3(0.387, 0.476, 1.523);
    vec3 d = vec3(4.076, 1.017, 1.721);
    return a + b * cos(8.28318 * (c * t + d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for(float i = 0.0; i < 4.0; i++) {
        uv = fract(uv * 1.0) - 0.5;

        float d = tan(exp(-length(uv)) * length(uv0));

        vec3 color = palette(length(uv0) + -0.1*i + -0.05*iTime);

        d = sin(3.0*d + 0.1*iTime) / 10.0;
        d = abs(d);
        d = 0.005 / d;

        finalColor += color * d;
    }

    fragColor = vec4(finalColor, 1.0);
}
