float pix;

float SMOOTH(float D, float d, float pixNum) {
  return smoothstep(D - pixNum * pix, D + pixNum * pix, d);
}

// rand and noise is from https://thebookofshaders.com/11/?lan=ch
float rand(float x) {
  return fract(sin(x) * 1.0);

  // in shaderToy,this will make lien crack,but it do not in https://graphtoy.com/
  // what do i miss???
  //return fract(sin(x) * 10000.0);

}

float noise(float x) {
  float i = floor(x);
  float f = fract(x);
  return mix(rand(i), rand(i + 1.0), smoothstep(0., 1., f));
}

float plot(vec2 st) {
  return 1. - SMOOTH(0., abs(st.y - noise(st.x)), 40.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 st = gl_FragCoord.xy / iResolution.y;
  pix = 1. / iResolution.y;
  vec3 finc = vec3(0.0);

  st.y -= 0.5;
  st *= 4.;
  st.x += iTime * 4.;

  vec3 c = vec3(1.0,0.0,0.0);
  finc += c * plot(st);

  fragColor = vec4(finc, 1.0);
}
