void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    if (myTime < PAUSE)
    {
        fragColor = texture(iChannel2, uv);
        return;
    }

    if (fract(myTime) < PAUSE) fragColor = texture(iChannel1, uv);
    else                       fragColor = texture(iChannel0, uv);   
}

//Hash without Sine by Dave_Hoskins
//https://www.shadertoy.com/view/4djSRW
float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

//glsl-easings by hughsk
//https://github.com/glslify/glsl-easings/blob/master/cubic-out.glsl
float cubicOut(float t) {
  float f = t - 1.0;
  return f * f * f + 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =fragCoord/iResolution.xy;

    float a = hash11(floor(myTime)) * PI * 2.0;
    vec2 dir = vec2(cos(a),sin(a));
    float v = step(0., dot(uv - vec2(0.5), dir));
    uv = uv + (vec2(v) * 2.0 - 1.0) * vec2(-dir.y, dir.x) * cubicOut(fract(myTime)) * 0.16;

    fragColor = texture(iChannel0, uv); 
}

#define myTime iTime*2.
const float PAUSE = 0.03;
const float PI = 3.141592;
