void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 finCol = vec3(0.0);
    vec3 col = vec3(0.8, 0.4, sin(iTime));

    uv = fract(uv * 1.2);
    uv -= 0.5;
    float d = fract(length(uv) * 5.0 + iTime);
    d = smoothstep(-0.1, 0.9, d);

    finCol += 0.8 * d * col;

    col = vec3(0.8, sin(iTime) - cos(iTime), 0.7);

    uv = fract(uv * 1.3);
    uv -= 0.5;
    d = fract(length(uv) * 5.0 + iTime);
    d = smoothstep(-0.1, 1.5, d);

    finCol += 0.8 * d * col;

    col = vec3(cos(iTime), 0.4, 0.7);

    uv = fract(uv * 1.4);
    uv -= 0.5;
    d = fract(length(uv) * 5.0 + iTime);
    d = smoothstep(-0.1, 1.5, d);

    finCol += 0.3 * d * col;

    col = vec3(cos(iTime), sin(iTime), 0.7);

    uv = fract(uv * 3.0);
    uv -= 0.5;
    d = fract(length(uv) * 5.0 + iTime);
    d = smoothstep(-0.1, 1.5, d);

    finCol += 0.3 * d * col;

    fragColor = vec4(finCol,1.0);
}
