#define PI 3.14159265
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy)*2.-1.;
    uv.x *= iResolution.x/iResolution.y;
    //uv *= 2.;
    //uv.x += iTime;

    vec3 col = vec3(0);
    float a = atan(uv.x,uv.y)/2.+iTime;
    col.r += abs(cos(a));
    col.g += abs(cos(a+(4./3.)*PI));
    col.b += abs(cos(a+(2./3.)*PI));

    col *= smoothstep(1.,.8,length(uv))-smoothstep(.8,.6,length(uv));
    fragColor = vec4(col,1.0);
}
