#define radius 200.0
#define edge 15.0

float sdfRound(vec2 dir, float r){
    return length(dir)-r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 pos=iResolution.xy/2.0;

    vec4 col;

    vec2 start=vec2(0,0);
    //float edge2=8.0*clamp(cos(iTime),0.0,1.0);
    vec3 mid=vec3(0,1.0,0.0);

    vec3 fromk=normalize(vec3(fragCoord-pos,0.0));

    float runtimeEdge=1.5* sin(20.0* acos( dot(mid,fromk)));
    float roundF=sdfRound(pos-fragCoord, radius);

    float final=runtimeEdge*10.0*sin(iTime);

    if(roundF>edge+final){
        col=vec4(1,1,1,1);
    }else if(roundF<final){
        col=vec4(0,0,0,1)+(clamp(sin(iTime),0.0,1.0)*vec4(0.5,0.0,0.5,1));
    }else{
        col=vec4(0.9,0.0,0.5,1);
    }



    // Output to screen
    fragColor =col;
}
