const float size = 3.;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;
    vec4 color = texture(iChannel0, uv);
    float l = (color.r+color.g+color.b)/3.;

    float tx = mod(fragCoord.x*1.5, size);
    float ty = mod(fragCoord.y*1.5, size);
    float t = (tx/size+ty)/size;

    float bw = l < t ? 0. : 1.;

    fragColor = vec4(bw,bw,bw, 0.);
}
