float hash13(vec3 p3)
{
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.zyx + 31.32);
    return fract((p3.x + p3.y) * p3.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 noise = vec3(uv.x * 100., uv.y * 100., iTime);

    if (hash13(noise) > 0.5) {
        discard;
    }

    vec3 col = vec3(0.);
    col.r = step(uv.y, sin(uv.x * 10. + iTime * 4.) * 0.2);

    float a = iTime * 2.;
    vec2 p = vec2(sin(a), cos(a)) * 0.4;
    col.g = 1. - step(0.075, length(p - uv));

    fragColor = vec4(col,1.0);
}
