#define T iTime
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    vec3 color = vec3(0.0);

    int iterations = 20;
    float scale = .99 + .0099*sin(T*.01);

    for (int i = 0; i < iterations; i++) {
        float a = 0.2+abs(sin(T*.01)); // 1
        mat2 rotR = mat2(cos(a * T * (.103 + float(i) * .01)), -sin(a * T * (.4334 + float(i) * .01)),
                         sin(a * T * (.153 + float(i) * .01)), cos(a * T * (.321 + float(i) * .01)));
        mat2 rotG = mat2(cos(a * T * (.203 + float(i) * .02)), -sin(a * T * (.3334 + float(i) * .02)),
                         sin(a * T * (.253 + float(i) * .02)), cos(a * T * (.221 + float(i) * .02)));
        mat2 rotB = mat2(cos(a * T * (.303 + float(i) * .03)), -sin(a * T * (.2334 + float(i) * .03)),
                         sin(a * T * (.353 + float(i) * .03)), cos(a * T * (.121 + float(i) * .03)));

        vec2 rP = rotR * (uv - 0.5) + 0.5;
        vec2 gP = rotG * (uv - 0.5) + 0.5;
        vec2 bP = rotB * (uv - 0.5) + 0.5;

        float freqX = 20.0 + 10.0 * sin(0.5 * T + float(i) * 0.1);
        float freqY = 50.0 + 25.0 * cos(0.7 * T + float(i) * 0.1);

        float mR = 0.75 + 0.25 * sin(T + float(i) * 0.1);
        float mG = 0.75 + 0.25 * cos(T + float(i) * 0.2);
        float mB = 0.75 + 0.25 * sin(T + float(i) * 0.3);

        color += vec3(mix(0.5 + 0.5 * cos(freqX * pow(scale, float(i)) * rP.x),
                         0.5 + 0.5 * cos(freqY * pow(scale, float(i)) * rP.y),
                         mR),
                      mix(0.5 + 0.5 * cos(freqX * pow(scale, float(i)) * gP.x),
                          0.5 + 0.5 * cos(freqY * pow(scale, float(i)) * gP.y),
                          mG),
                      mix(0.5 + 0.5 * sin(freqX * pow(scale, float(i)) * bP.x),
                          0.5 + 0.5 * sin(freqY * pow(scale, float(i)) * bP.y),
                          mB));
        uv *= scale;
    }

    color /= float(iterations);
        color = smoothstep(0.0, 1.0, color);

    fragColor = vec4(color, 1.0);
}
