float rand(vec2 co)
{
	return fract(sin(dot(co.xy, vec2(13.0, 13.0))) * 1000.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	if (mod(iTime, 1.96) > 1.95)
		uv.x += cos(iTime * 10.0 + uv.y * 1000.0) * 0.01;

    //if (mod(iTime, 4.0) > 3.0)
	//	uv = floor(uv * 32.0) / 32.0;

	if (mod(iTime, 1.3) < 0.0199)
    	uv += 1.0 / 64.0 * (2.0 * vec2(rand(floor(uv * 32.0) + vec2(32.05,236.0)), rand(floor(uv.y * 32.0) + vec2(-62.05,-36.0))) - 1.0);

	fragColor = texture(iChannel0, uv);

    if (rand(vec2(iTime)) < 0.075)
		fragColor = vec4(dot(fragColor.rgb, vec3(0.25, 0.5, 0.25)));

    fragColor.rgb += 0.15 * vec3(rand(iTime + fragCoord / vec2(-213, 5.53)), rand(iTime - fragCoord / vec2(213, -5.53)), rand(iTime + fragCoord / vec2(213, 5.53))) - 0.125;
}
