vec2 f1(vec2 a){
    return
        fract(a)
    ;
}

vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
    ,
    a1 = a+a2;
    return
        abs(f1((a1)*(a2.x+a2.y))-.5)
    ;
}

#define triwave_(p) abs(fract(.5+p/4.0)-.5)*2.
#define triwave1_(p) (abs(fract(p/8.0)-.5)-abs(fract(p/2.0)-.5)/2.)
float t1_(vec2 c, vec2 p){
    return triwave_((.5 - length( min(p=fract(p*sign(triwave1_(c*c.y))), 1.-p.yx) )));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 2.;
    vec2 draggedPos = texelFetch(iChannel0, ivec2(1,0), 0).xy;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    uv -= draggedPos/iResolution.y/t1;
    vec2 t2 = vec2(0.);
    bool b = uv.y>uv.x;
    for(int k = 0; k < 6; k++){

        //uv.y -= t1_(floor(uv),floor(uv));


        t2 =
            -triangle_wave(uv-.5)
        ;

        uv =
            t2-triangle_wave(uv.yx)
        ;




        uv =
            //abs(.5+uv/1.5+t2)
            abs(.5+uv+t2/1.5)
            //abs(.5+uv+t2)/1.5
        ;
        //uv = abs(fract(vec2(uv+vec2(.5,1.5))*2.)-.5)/2.;



        b =
            uv.y>uv.x
            //uv.y>uv.x || t2.y<t2.x
        ;
        if(b) uv = uv.yx;
        col =
            vec3(col.zx,max(uv.x-uv.y,0.))
        ;
    }
    fragColor = vec4(col*2.,1.0);
}

float fmod(float a, float b){
    //a += floor(a/(2.+b*b*4.));
    //a += (abs(mod(a,3.)-mod(a,5.)));
    a += mod(-b/2.,2.);
    return
        floor(mod(mod(floor(a/2.)*5.,b)*mod(floor(a/2.)*3.,b),b))
        //floor(mod(floor(a)*1.5,b))
    ;
}

float prime_rhythm(float time){
    return (1.+fmod(time/5.,2.))/(1.+fmod(time/3.,2.));
}

vec2 mainSound(int samp, float time){
  float s1 =
      8.,
  tempo1 =
      prime_rhythm(time)
  ,
  t = s1*time/4./tempo1,
  t4 = t/prime_rhythm(time+11.)/(1.+fmod(t/8.,2.)),
  t1 = fmod(t4,2.),
  t5 = t*2./prime_rhythm(time+7.),
  t3 = (1.+fmod(t5,2.)),
  t6 = time*8./t3,
  m3 =
      floor(fmod(t6,s1/2.)/2.),
  m2 =
      1. + fmod(t/t3/2.,s1/2.),
  a=
      (1.-log(fract(t/2./prime_rhythm(time+14.))/2.))*(1.-log(fract(t/prime_rhythm(time+7.))/4.)),
  nb =
      t*pow(2.,(m3+m2-t1*4.)/7.+7.5)*tempo1/2.
  ;
  return
      log(abs(.5-vec2(fract(nb*.998),fract(nb)))*a)
  ;
}

//from https://www.shadertoy.com/view/dtGGzK

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 col = vec4(0);
    if (floor(fragCoord) == vec2(1,0)) {
        vec2 prevpos = texelFetch(iChannel0, ivec2(1,0), 0).zw; //previous dragged mouse pos
        vec4 mp = texelFetch(iChannel0, ivec2(2,0), 0); //prev mouse pos
        vec4 m = iMouse; //current mouse pos

        //if mouse button up
        if (m.z < 0.0 && mp.z >= 0.0){
            col = texelFetch(iChannel0, ivec2(1,0), 0).xyxy;
        } else if (m.z >= 0.0){
            col = vec4(m.xy - abs(m.zw) + prevpos, prevpos);
        } else {
            col = texelFetch(iChannel0, ivec2(1,0), 0);
        }
    }
    //the last mouse position
    if (floor(fragCoord) == vec2(2,0)) {
        col = iMouse;
    }
    fragColor = col;
}
