void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/ iResolution.xy;
    float c;
    float size = 10.0;
    uv.x *= size;
    uv.y *= floor(size * (iResolution.y / iResolution.x));

#if 0

    uv = floor(uv);
    c = uv.x + uv.y;
    c = mod(c, 2.0);

#endif

#if 0

    uv = fract(uv);
    c = step(0.5, uv.x) + step(0.5, uv.y);
    c = mod(c, 2.0);

#endif

# if 1

    uv = fract(uv);
    uv -= 0.5;
    c = step(0.0, uv.x * uv.y);

#endif

    fragColor = vec4(vec3(c),1.0);
}
