const float pi = 3.141592654;
const float wave_amp = 10.;
float sdf_sph1 (vec2 p){
    float rad = .0;
    vec2 pos = vec2(0);
    return length(p-pos)-rad+.5*sin(p.x*100.+iTime*pi);

}
float sdf_sph2(vec2 p){
    float rad = .0;
    vec2 pos=vec2(0);
    return length(p-pos)-rad+.5*sin(p.y*100.+iTime*pi*2.);

}
float sdf(vec2 p){
    return (sdf_sph1(p)+(sdf_sph2(p)-sdf_sph1(p))*(.5+.5*sin(iTime*pi*.5+pi*1.5)));
}
vec4 render(vec2 uv,float pixsamp){
    uv=round(uv*pixsamp)/pixsamp;
    vec4 col=vec4(1);
    col+=vec4(-vec3(step(0.,sdf(uv))),1.);
    return col;

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = .75*(2.*fragCoord-iResolution.xy)/iResolution.y;
    float ph=length(uv)*wave_amp*sin(iTime*pi*.5);
    vec2 dist=vec2(uv.x*sin(ph)-uv.y*cos(ph),uv.y*sin(ph)+uv.x*cos(ph));
    fragColor = (render(dist,250000.)+vec4(.2,.0,.3,1.))*vec4(.8,.5,.8,1.);
    //fragColor.rgb=1.0-fragColor.rgb;
}
