void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv.x += sin(fract(sin(uv.y+iTime/10.)));
    uv.y += cos(fract(cos(uv.x+iTime/10.)));

    vec3 col = vec3(sin(fract(sin(uv.x-iTime/25. + uv.y+iTime/25.))), cos(fract(cos(uv.x-iTime/21. + uv.y+iTime/32.))), sin(fract(cos(uv.x-iTime/11. + uv.y+iTime/46.))));

