void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float shift = 32.0;
    float offset = -4.0;

    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb;

    float l = sin((iTime * 0.2 + uv.y) * 20.0);
    l = l * 0.5 + 0.5;
    l = pow(l, 2.0);

    col *= (l * shift) + offset;
    col = floor(col);
    col /= (l * shift) + offset;

    fragColor = vec4(col,1.0);
}
