//black point
vec3 bp(vec3 col, float x){ return max(col, vec3(x)); }
vec3 bp(vec3 col, vec3 x){ return max(col, x); }

//white point
vec3 wp(vec3 col, float x){ return min(col, vec3(x)); }
vec3 wp(vec3 col, vec3 x){ return min(col, x); }




//makes colors black in shadows
vec3 bpn(vec3 col, float x){
    return (col-x)/(1.-x); 
}
vec3 bpn(vec3 col, vec3 x){
    return (col-x)/(vec3(1.)-x);
}

//makes colors burnt in highlights
vec3 wpn(vec3 col, float x){
    col = col/x; 
    return col;
}
vec3 wpn(vec3 col, vec3 x){
    col = col/x;
    return col;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = fragCoord/iResolution.xy;


    vec3 col = vec3(uv.y); //just gradient
    col = texture(iChannel0, uv).xyz;

    //col = bpn(col, .2);
    //col = wpn(col, .8);
    //col = bpn(col, vec3(.0, .1, .2));
    //col = wpn(col, vec3(1., .7, .8));

    col = bp(col, vec3(.3,0.,.3) );
    col = wp(col, vec3(.7,.9,.7) );


    fragColor = vec4(col,1.0);
}
