/* "Cubic Dreams" by @kishimisu (2023) - https://www.shadertoy.com/view/DlSXzy
   [455 chars]

   Space repetition, space mirroring and NEONS!
*/

void mainImage(out vec4 O, vec2 u) {
    vec2 R = iResolution.xy; u += u-R;
    vec3 p, q;
    float i = 0., d = i, a, t = iTime*.3;

    for (O *= i; i++ < 60.;)  
        p = d*normalize(vec3(abs(u/R.y)*mat2(cos(t+d*.15+vec4(0,33,11,0))),1)),
        p.z += t,

        p = abs(fract(p+.5)-.5) - cos(t)/5.7 - .325,
        q = abs(p+.01)-.01,

        d += a = min(min(
          length(max(vec3(p.x, q.yz)  , 0.)) ,
          length(max(vec3(q.x,p.y,q.z), 0.))),
          length(max(vec3(q.xy, p.z)  , 0.))), 

        O += pow(max(cos(d*(1.+sin(t*.5)*.3) + .5*vec4(0,1,2,0)), 0.), O-O+4.)
             /(1.+abs(a)*5.)/exp(d*.15)/4.;
}
