mat3 rotate3D(float angle, vec3 axis){
    vec3 a = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float r = 1.0 - c;
    return mat3(
        a.x * a.x * r + c,
        a.y * a.x * r + a.z * s,
        a.z * a.x * r - a.y * s,
        a.x * a.y * r - a.z * s,
        a.y * a.y * r + c,
        a.z * a.y * r + a.x * s,
        a.x * a.z * r + a.y * s,
        a.y * a.z * r - a.x * s,
        a.z * a.z * r + c
    );
}

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e;vec3 p,q;
o=vec4(0.);

for(i=0.;i++<128.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y, e-.001); 
    p*=rotate3D(iTime, vec3(1));

    for(j=0.;j++<64.;){
        p=abs(abs(p)-.001)-.002,
        q=vec3(p.y-p.x, p.x-p.y, p.x+p.y+p.z);
        if(d<2.)d=length(q);
        d-=dot(q,q)/1e4;
    }e+=d;
}o+=vec4(log(p),.5)*3./i;}
