// Author: Sara01
// Title: Shader Recreation of Piet Mondrian - Tableau (1921)

const float PI = 3.141592;

vec2 Rotate(vec2 uv, float angleDegrees) {
    float angleRads = angleDegrees * (PI / 180.0);
    float s = sin(angleRads);
    float c = cos(angleRads);
	mat2 rotationMat = mat2(c, -s, s, c);    

    return uv * rotationMat;
}

float DrawLine(vec2 uv, vec2 pos, float thickness, float angleDegrees) {
    normalize(pos);
    uv -= pos; // same as uv = vec2(uv.x - pos.x, uv.y - pos.y) or uv = uv - pos;
    uv = Rotate(uv, angleDegrees);

    float t = thickness;
    float line = step(0.1 - t, uv.x) - step(0.1 + t, uv.x);

    return line;
}

float Clamp01(float value) {
    return clamp(value, 0.0, 1.0);
}

float Rectangle(vec2 uv, vec4 sides) {
    vec2 bottomLeft = step(vec2(sides.x, sides.y), uv);
    float blArea = bottomLeft.x * bottomLeft.y;
    vec2 topRight = step(vec2(sides.z, sides.w), 1.0 - uv);
    float trArea = topRight.x * topRight.y;

    float rectangle = blArea * trArea;
    return rectangle;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    vec3 white = vec3(0.961,0.933,0.871);
    vec3 black = vec3(0.110,0.122,0.141);
    vec3 red = vec3(0.686,0.137,0.137);
    vec3 blue = vec3(0.000,0.373,0.608);
    vec3 yellow = vec3(0.996,0.776,0.180);

    vec3 canvasColor = white;

    // Rectangles
    float redRect = Rectangle(uv, vec4(0.0, 0.6, 0.8, 0.0));
    float blueRect = Rectangle(uv, vec4(0.74, 0.0, -1.0, 0.9));
    float yellowRect = Rectangle(uv, vec4(0.94, 0.6, -1.0, 0.0));

    canvasColor = mix(canvasColor, red, redRect);
    canvasColor = mix(canvasColor, blue, blueRect);
    canvasColor = mix(canvasColor, yellow, yellowRect);

    // Lines
    float lineVert1 = DrawLine(uv, vec2(-0.030,-0.010), 0.012, .0);
    float lineVert2 = DrawLine(uv, vec2(0.100,-0.010), 0.012, .0);
    float lineVert3 = DrawLine(uv, vec2(0.630,0.020), 0.012, .0);
    float lineVert4 = DrawLine(uv, vec2(0.840,0.000), 0.012, .0);

    float lineHor1 = DrawLine(uv, vec2(-0.270,0.890), 0.015, 90.0);
    float lineHor2 = DrawLine(uv, vec2(-0.240,0.690), 0.015, 90.0);
    float lineHor3 = DrawLine(uv, vec2(-0.240,0.190), 0.012, 90.0);


    float linesSum = Clamp01 (
          lineVert1 + lineVert2 + lineVert3 + lineVert4
        + lineHor1 + lineHor2 + lineHor3
    );

    canvasColor = mix(canvasColor, black, linesSum);

    float whiteRect = Rectangle(uv, vec4(0.0, 0.0, 0.8125, 0.422));

    canvasColor = mix(canvasColor, white, whiteRect);



    fragColor = vec4(canvasColor, 1.0);
}
