#define R iResolution

void mainImage(out vec4 k, vec2 c)
{
    // Remapping
    float t = 0.3 * iTime;
    vec2 u = (1.5 * c - 0.75 * R.xy) / R.y; 

    // Rotation matrix
    vec2 N = vec2(cos(t), sin(t));
    mat2 m = mat2(N.x, -N.y, N.y, N.x);

    for (int i = 0; i < 9; i ++) 
    {
        u *= 2.1 * m; // Scale and rotate
        u = abs(u); // Fold
        u -= .5; // Translate
    }
    k = vec4(1. - length(u));
}
