void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = (2.*fragCoord.xy-iResolution.xy) / iResolution.y; 

    float a = atan(uv.x,uv.y)-sin(iTime)*.5;
    fragColor = vec4(vec3(abs(.05/a)),1.);


    //直线
    vec3 l = vec3(length(uv.y));//
    fragColor += vec4(0.01/l,1.);


    //空心圆
    vec3 r = vec3(length(uv));
    fragColor += vec4(vec3(abs(.05/(r-.5))),1.);


    //实心圆
    vec3 r2 = vec3(length(vec2(uv.x-((fract(iTime*2.)*1.)*4.-2.),uv.y)));
    fragColor += vec4(vec3(.05/r2),1.);


}
