#define PI 3.14159265
vec3 col = vec3(0.0);

void cir(vec2 uv, vec2 center, float r, vec4 cirCol)
{
	float t = smoothstep(r, r-0.005, distance(uv, center));
	col = mix(col, cirCol.rgb, t*cirCol.a);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
  vec2 uv = (2.0*fragCoord.xy - iResolution.xy)/ iResolution.y;
	vec2 center = vec2(0.0);
	float r = 1.0;
	float a = 0.0;
	vec4 cirCol = vec4(1.0, 1.0, 1.0, 0.3);

    //change dr and da and see what happens
	float dr = 0.5;
	float da = PI/2.0;
	da = mod(iTime*0.5, 2.0*PI);
	dr = (iMouse.x+1.0)/2.0;
    dr = 0.9;

	const int MAX = 64;
	for (int i = 0; i < MAX; i++)
	{
		if (mod(float(i), 3.0) == 0.0)
			cir(uv, center, r, vec4(1.0, 0.0, 0.0, 1.0));
		else if (mod(float(i), 3.0) == 1.0)
			cir(uv, center, r, vec4(0.0, 1.0, 0.0, 1.0));
		else
			cir(uv, center, r, vec4(0.0, 0.0, 1.0, 1.0));
		a += da;
		center += (r-r*dr)*vec2(cos(a), sin(a));
		r *= dr;
	}

	fragColor = vec4(col, 1.0);
}
