void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy; // <0, 1>
    uv.x -= 0.25;
    uv.x *= iResolution.x/iResolution.y; // fix aspect ratio

    float col = 0.0;
    float radius = 0.25;
    col += (1.0 - step(radius, length(uv - vec2(0.5, 0.25)))) + (1.0 - step(radius, length(uv - vec2(0.5, 0.75))));
    vec2 square = step(abs(uv - 0.5), vec2(radius, radius));
    col += square.x * square.y;

    col = clamp(col, 0.0, 1.0);
    // Output to screen
    fragColor = col * vec4(1.0, 0.0, 0.0, 1.0);
}
