// Down to 137 after Fabrice magic
void mainImage(out vec4 k, vec2 c)
{
    c /= iResolution.y;
    vec2 f = floor(c);

    for ( k=vec4(1); k.a++ < 7. && f != k.xy; )
        f = floor( c = 3.*(c - f) );

    f == k.xy ? k*=0. : k;
}
