#define DOT_SCALE 1.0
//#define DOT_PIX vec2(64,36)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord / iResolution.xy) * 2.0 - 1.0;

    float ny = 40.;
    float nx = ny * iResolution.x / iResolution.y;
    vec2 dotPix = vec2(nx, ny);
    //dotPix = DOT_PIX;

    vec2 uvDot = (((floor(uv * dotPix) + 0.5) / dotPix) + 1.0) * 0.5;
   	vec3 lum = texture(iChannel0, uvDot).rgb;

    vec2 uvGrid = fract(uv * dotPix);
    vec2 pGrid = uvGrid - 0.5;
    //vec3 col = (lum + 0.05) * 5.0 * clamp(0.5 - length(pGrid),0.,1.);
    vec3 col = lum * 2.2* smoothstep(0.5*DOT_SCALE, 0., length(pGrid));
    fragColor = vec4(col, 1.0);
}
