float smoothMax(in float a, in float b, in float k) {
    return log(exp(k*a)+exp(k*b))/k;
}
float smoothMin(in float a, in float b, in float k) {
    return -smoothMax(-a,-b,k);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
      vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y; // Condense 3 lines down to a single line!
      vec3 camera = vec3(0.0,0.0,-0.1);
      vec3 ro = vec3(camera);
      vec3 onestep = normalize(ro-vec3(uv,-1.0));
      int MAXSTEPS = 300;
      bool hit = false;
      for(int i = 0; i < MAXSTEPS; i++) {
          float dist = length(ro - vec3(0.2,0.0,1.3))-0.01;
          float dist2 = length(ro - vec3(-(iMouse.xy- iResolution.xy/2.0)/iResolution.y,1.6))-0.005;
          float cDist = smoothMin(dist,dist2,2.3); 
          if(cDist <=0.01) {
              hit = true;
              break;
          }
          ro+=onestep*cDist;
      }
      if(hit) fragColor = vec4(sin(iTime),cos(iTime),ro.z/1.3,1.0);
      else fragColor = vec4(0.0,0.0,0.0,1.0);
}
