vec4 transitionSide(vec2 uv, vec4 color, float progression) {
    const float transitionSideDiamondPixelSize = 48.0f;

    uv.x = 1.0 - uv.x;

    float xFraction = fract(gl_FragCoord.x / transitionSideDiamondPixelSize);
    float yFraction = fract(gl_FragCoord.y / transitionSideDiamondPixelSize);

    float xDistance = abs(xFraction - 0.5);
    float yDistance = abs(yFraction - 0.5);

    if (xDistance + yDistance + uv.x + uv.y < progression * 4.0f) {
        color = vec4(0.0, 0.0, 0.0, 1.0);
    }

    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 color = vec4(1.0, 1.0, 1.0, 1.0);

    float progression = (sin(iTime) + 1.0) / 2.0;
    color = transitionSide(uv, color, progression);

    fragColor = color;
}
