//Excellent Video Explaination: https://www.youtube.com/watch?v=PMltMdi1Wzg
float SDsegment(vec2 p, vec2 a, vec2 b, float R)
{
    float h = min(1.0, max(0.0, dot(p-a, b-a)/dot(b-a, b-a)));
    return length(p-a - (b-a)*h) - R;
}

float H(vec2 p, vec2 a, vec2 b, float R)
{
    float h = min(1.0, max(0.0, dot(p-a, b-a)/dot(b-a, b-a)));
    return h;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord / iResolution.y) - vec2(0.5 * iResolution.x / iResolution.y, 0.5);
    vec2 m = (iMouse.xy / iResolution.y) - vec2(0.5 * iResolution.x / iResolution.y, 0.5);

    float d = SDsegment(uv, m, vec2(0.3,0.3), 0.1);

    fragColor = vec4(sin(200.0*d + 10.0*iTime), 0.0, 0.4, 1.0);
}
