vec3 pallete(float t) {
    vec3 a = vec3(0.718, 0.498, -0.222);
    vec3 b = vec3(-0.032, 0.588, 0.968);
    vec3 c = vec3(1., 1., 1.);
    vec3 d = vec3(0., 0.333, 0.667);

    return a + b * cos( 1. * (c * t + d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    uv *= iTime * 1.;
    uv = fract(uv);
    uv -= 0.5;

    float d = length(uv);

    vec3 col = pallete(d + iTime);

    d = sin(d * 8. + iTime) / 8.;
    d = abs(d);
    d = 0.02 / d;

    col *= d;

    fragColor = vec4(col, 1.);
}
