// partly unrolled variant of https://shadertoy.com/view/DtVyRt
// reproducing https://twitter.com/Pixelated_Donut/status/1726602167019241671
//             https://twitter.com/Yugemaku/status/1726559476172616047
// cf https://www.desmos.com/calculator/zqhgc6pe9e

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy,
          U = ( u+u - R ) / R.y;
    float N = 2e2, i0 = round(N*dot(U,U)),  F = 1.618, i;    
    O *= 0.;
    if ( i0 < N )
    for( i = i0-5.; i++ < i0+5.; )  // F = (1+sqrt(5))/2
        O += smoothstep( 3./R.y, 0.,       
               length( U - cos( 6.283*(2.*F*i - pow(fract(F*i-.2*iTime),1.5)) + vec2(11,0) ) *sqrt(i/N) )
               -.01 );
         // * ( .6 + .6 * cos( 6.3*(fract(F*i-.2*iTime))  + vec4(0,23,21,0)  ) );   // coloring
}
