vec4 black = vec4(0, 0, 0, 1);
vec4 white = vec4(1, 1, 1, 1);

float noise(float x) {
    return sin(x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(1, 1, 1, 1);
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = 2. * uv - 1.;
    uv.x *= iResolution.x / iResolution.y ;
    uv *= 2.;
    float x_axis = step(abs(mod(uv.y, 0.25)), 0.01);
    fragColor = mix(fragColor, vec4(177./255.,168./255.,231./255.,1), x_axis);
    float y_axis = step(abs(mod(uv.x, 0.25)), 0.01);
    fragColor = mix(fragColor, vec4(177./255.,168./255.,231./255.,1), y_axis);
    x_axis = step(abs(mod(uv.y, 1.)), 0.02);
    fragColor = mix(fragColor, vec4(177./255.,168./255.,231./255.,1), x_axis);
    y_axis = step(abs(mod(uv.x, 1.)), 0.02);
    fragColor = mix(fragColor, vec4(177./255.,168./255.,231./255.,1), y_axis);

    float y = 0.;
    float x = uv.x;
    float amplitude = 1.;
    float frequency = 1.;

    int octaves = 10;
    float lacuarity = 2.;
    for (int i = 0; i < octaves; ++i) {
        y += amplitude * noise(frequency * (x + iTime));
        frequency *= lacuarity;
        amplitude *= 0.5;
    }
    y = smoothstep(0.02, 0., abs(y - uv.y));

    fragColor = mix(fragColor, black, y); 
}
