vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
    ,
    a1 = a+a2;
    return abs(fract((a1)*(a2.x+a2.y))-.5);
}

float scale = 1.5;

void transform(inout vec2 uv, inout vec2 t2, inout vec3 col,inout float c1){
        uv.y -= 1./4.;

        uv = (uv+t2)/scale;

        uv = (fract(vec2(uv+vec2(.5,1.5))*scale)-.5)/scale;

        t2 = triangle_wave(uv-.5);
        uv =
            //t2-triangle_wave(uv.yx)+fract(t2/2.)

            //looks better with more iterations
            t2-triangle_wave(uv.yx)-fract(t2/2.)
        ;
}

vec3 fractal(vec2 uv){
    vec3 col = vec3(0.);
    vec2 t2 = vec2(0.);
    vec3 col1 = col;
    float c1=0.;
    for(int k = 0; k < 12; k++){

        vec2 uv_1 =
            uv
            //uv/2.
            //uv+sin(uv.x/4.)*4.+1.;
            //uv*2.
            //uv/2.+vec2(sin(uv.y*2.),cos(uv.x*2.))
        ;
        vec2 t2_1 =
            t2
            //t2*2.
            //t2*2.
            //t2/2. + vec2(sin(uv.y*2.),cos(uv.x*2.))
            //sin(uv*2.)/2.
        ;

        transform(uv,t2,col,c1);
        transform(uv_1,t2_1,col,c1);



        c1 =
            //max(abs(uv.y-uv.x*sign(uv.x-uv.y))/2.,c1)
            max(abs(uv_1.y-uv_1.x)/2.,c1)
        ;
        c1 =
            max(abs(2.*c1-1.),c1/4.)

            //more colorful
            //abs(2.*c1-1.)

            //Spooky!
            //max(abs(2.*c1-2.),c1/4.)/2.
        ;
        col.x =
            //max(max(length(uv-t2-c1),abs(uv.y-uv.x))/3.,col.x)
            max(max(length(uv_1-t2_1-c1),abs(uv_1.y-uv_1.x)),col.x)/3.

            //even more colorful   
            //2.-max(max(length(uv-t2-c1),abs(uv.y-uv.x))/3.,col.x)

            //blue and gold
            //2. - max((abs(uv.y-uv.x))/3.,col.x)

            //black and white
            //col.x
        ;
        col =
            abs(col-(1.-(c1*col.x)));
        col1 =
            abs(col1*c1-col-1.).yzx;
    }
    return col1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
