float fmod(float a, float b){
    for(float i = 1.; i < mod(floor(a/b),5.); i++){
        a += mod(floor(a/2.),b)*i;
    }
    float a1 = a;
    return
        mod(floor(a),b)
    ;
}

#define fract1(a) pow(fract(a),1.+log(2.)-log(1.+fract(a)))

vec2 synth(float a1,vec2 a,float a2,float a3,vec2 nb,float a4){
    return log(a1+a*abs(a2-a3*abs(.5-vec2(fract1(nb.x*.998),fract1(nb.y)))*a4));
}

